  // }

  const handleSubmit = (event) =>{
    event.preventDefault()
    try{app.auth().signInWithEmailAndPassword(email, password)}
    catch{
      console.error(err);
      alert(err.message);
    }
    // .then
    // (()=>{
    //   navigate("/home")
    // })
  }

    let navigate = useNavigate();
  // const [user, setUser] = useState({
